mapper.select:
select id, host_id, cpu_usage, memory_usage, network_conn, disk_usage, disk_info, file_status, monitor_time, create_time 
from host_status_monitor


        and host_id = #{hostId}


        and monitor_time >= #{monitorTimeStart}


        and #{monitorTimeEnd} >= monitor_time


order by create_time desc

select * from process_monitor


        and process_id = #{processId}


        and process_name like concat("%", #{processName}, "%")


        and process_status = #{processStatus}


order by create_time desc
